mapper.select:
SELECT opt_code
FROM car_option
WHERE car_seq = #{seq}

mapper.delete:
DELETE
FROM CAR
WHERE SEQ = #{seq}
  AND cmpn_seq = #{cmpnSeq}

mapper.update:
UPDATE car


        car_num = #{car.carNum}


        brand = #{car.brand}


        model = #{car.model}


        segment = #{car.segment}


        trim = #{car.trim}


        color = #{car.color}


        year = #{car.year}


        fuel = #{car.fuel}


        passenger = #{car.passenger}


        mission = #{car.mission}


        trunk = #{car.trunk}


        kmpl = #{car.kmpl}


        price = #{car.price}

    upt_date = SYSDATE

WHERE seq = #{car.seq}
AND cmpn_seq = #{cmpn.seq}
AND status NOT IN('CST02, CST03')

UPDATE car
SET status = #{status},
    upt_date = SYSDATE
WHERE seq = #{seq}
  AND cmpn_seq = #{cmpnSeq}

mapper.select:
SELECT * FROM (SELECT /*+ INDEX_DESC(CAR IDX_CAR) */ ROWNUM RN, a.* FROM (SELECT ROW_NUMBER() OVER(PARTITION BY MODEL ORDER BY SEQ ) CNT, CAR.* FROM CAR) a
AND CNT = 1 AND ROWNUM <= #{pageNum} * #{amount})
WHERE RN > (#{pageNum} - 1) * #{amount}

mapper.insert:
    	INSERT INTO CAR_OPTION(
    						 OPT_CODE,
    						 CAR_SEQ
							)
					VALUES(
							#{optCode},
							#{carSeq}
						  )

mapper.select:
SELECT * FROM ( SELECT ROWNUM RN, C.seq, C.car_num, C.brand, C.model, C.segment, C.trim, C.color, C.year, C.fuel, C.passenger, C.mission, C.trunk, C.door, C.kmpl, C.price, C.status, C.img1, C.img2, C.img3, C.gen_date, C.upt_date, CM.name AS cmpn_name, CM.postal AS cmpn_postal, CM.addr1 AS cmpn_addr1, CM.addr2 AS cmpn_addr2, CM.tel AS cmpn_tel, CO.opt_code AS co_opt_code, IO.opt_code AS io_opt_code, IO.price AS io_price FROM ( SELECT /*+ INDEX_DESC(CAR IDX_CAR) */ * FROM car
ROWNUM <= #{pageNum} * #{amount}
) C LEFT OUTER JOIN company CM ON C.cmpn_seq = CM.seq LEFT OUTER JOIN car_option CO ON C.seq = CO.car_seq LEFT OUTER JOIN insurance_option IO ON C.seq = IO.car_seq ) WHERE RN > (#{pageNum} - 1) * #{amount}
