mapper.select:
SELECT * FROM ( SELECT ROWNUM RN, EH.seq, EH.brand, EH.segment, EH.model, EH.trim, EH.options, EH.items, EH.start_date, EH.end_date, EH.memo, EH.take_place_code, EH.take_place_basic, EH.take_place_detail, EH.gen_date, EH.upt_date, BC.name AS brand_name, MC.name AS model_name, MC.img AS model_img, TC.name AS trim_name, TC.fuel AS trim_fuel, CS.name AS segment_name, M.seq AS mem_seq FROM ( SELECT /*+ INDEX_DESC(ESTIMATE_HISTORY IDX_ESTIMATE_HISTORY) */ * FROM estimate_history
ROWNUM <= #{pageNum} * #{amount}
) EH LEFT OUTER JOIN brand_code BC ON EH.brand = BC.code LEFT OUTER JOIN model_code MC ON EH.model = MC.code LEFT OUTER JOIN trim_code TC ON EH.trim = TC.code LEFT OUTER JOIN code_sub CS ON EH.segment = CS.code LEFT OUTER JOIN member M ON EH.mem_seq = M.seq ) WHERE RN > (#{pageNum} - 1) * #{amount}

SELECT * FROM ( SELECT ROWNUM RN, EH.seq, EH.options, EH.items, EH.start_date, EH.end_date, EH.memo, EH.take_place_code, EH.take_place_basic, EH.take_place_detail, EH.gen_date, EH.upt_date, BC.name AS brand, MC.name AS model, MC.img, TC.name AS trim, TC.fuel, CS.name AS segment, M.seq AS mem_seq FROM (SELECT * FROM estimate_history
mem_seq = #{seq} AND ROWNUM <= #{cri.pageNum} * #{cri.amount}
) EH LEFT OUTER JOIN brand_code BC ON EH.brand = BC.code LEFT OUTER JOIN model_code MC ON EH.model = MC.code LEFT OUTER JOIN trim_code TC ON EH.trim = TC.code LEFT OUTER JOIN code_sub CS ON EH.segment = CS.code LEFT OUTER JOIN member M ON EH.mem_seq = M.seq ) WHERE RN > (#{cri.pageNum} - 1) * #{cri.amount}

SELECT * FROM ( SELECT ROWNUM RN, ESH.seq, ESH.items, ESH.price, ESH.memo, ESH.gen_date, ESH.upt_date FROM ( SELECT /*+ INDEX_DESC(ESTI_SUBMIT_HISTORY IDX_ESTI_SUBMIT_HISTORY) */ * FROM esti_submit_history
esti_seq = #{seq} AND ROWNUM <= #{cri.pageNum} * #{cri.amount}
) ESH LEFT OUTER JOIN estimate_history EC ON ESH.esti_seq = EC.seq LEFT OUTER JOIN company CM ON ESH.cmpn_seq = CM.seq LEFT OUTER JOIN car C ON ESH.car_seq = C.seq ) WHERE RN > (#{cri.pageNum} - 1) * #{cri.amount}
