SELECT * FROM ( SELECT /*+ INDEX_DESC(tb IDX_ESTIMATE_HISTORY) */ ROWNUM RN, seq, brand, segment, model, trim, options, items, start_date, end_date, take_place_code, take_place_basic, take_place_detail, gen_date, upt_date, mem_seq FROM estimate_history tb
ROWNUM <= #{pageNum} * #{amount}
) WHERE RN > (#{pageNum} - 1) * #{amount}
