mapper.select:
SELECT * FROM ( SELECT /*+ INDEX_DESC(tb IDX_ESTIMATE_HISTORY) */ ROWNUM RN, seq, brand, segment, model, trim, options, items, start_date, end_date, take_place_code, take_place_basic, take_place_detail, gen_date, upt_date, mem_seq FROM estimate_history tb
ROWNUM <= #{pageNum} * #{amount}
) WHERE RN > (#{pageNum} - 1) * #{amount}

mapper.update:
UPDATE estimate_history
brand = #{brand}, segment = #{segment}, model = #{model}, trim = #{trim}, start_date = #{startDate}, end_date = #{endDate}, options = #{options}, items = #{items}, take_place_code = #{takePlaceCode}, take_place_basic = #{takePlaceBasic}, take_place_detail = #{takePlaceDetail}, upt_date = SYSDATE,
WHERE seq = #{seq}
