mapper.insert:

	SELECT SEQ_NOTICE.NEXTVAL FROM DUAL

INSERT INTO NOTICE ( SEQ
				   , TITLE
				   , CONTENT
				   , WRITER)
VALUES ( #{seq}
	   , #{title}
	   , #{content}
	   , #{writer})

mapper.update:
UPDATE NOTICE
SET TITLE = #{title}
  , CONTENT = #{content}
  , WRITER = #{writer}
WHERE SEQ = #{seq}
