mapper.insert:

	SELECT SEQ_RENT_HISTORY.nextval FROM DUAL

INSERT INTO RENT_HISTORY(
			SEQ
			, RENT_TYPE
			, START_DATE
			, END_DATE
			, RECEIVER
			, PRICE
			, ESTIMATE
			, STATUS
			, TAKE_PLACE_CODE
			, TAKE_PLACE_BASIC
			, TAKE_PLACE_DETAIL
			, MERCHANT_UID
			, PAY_METHOD
			, CAR_SEQ
			, MEM_SEQ
)
VALUES(
		#{seq}
		, #{rentType}
		, #{startDate}
		, #{endDate}
		, #{receiver}
		, #{price}
		, #{estimate}
		, #{status}
		, #{takePlaceCode}
		, #{takePlaceBasic}
		, #{takePlaceDetail}
		, #{merchantUid}
		, #{payMethod}
		, #{carSeq}
		, #{memSeq}
)
