mapper.insert:

			SELECT SEQ_RENT_HISTORY.nextval FROM DUAL

		INSERT INTO RENT_HISTORY(
					SEQ
					, RENT_TYPE
					, START_DATE
					, END_DATE
					, RECEIVER
					, PRICE
					, STATUS
					, TAKE_PLACE_CODE
					, TAKE_PLACE_BASIC
					, TAKE_PLACE_DETAIL
					, MERCHANT_UID
					, PAY_METHOD
					, CAR_SEQ
					, MEM_SEQ
                    , cmpn_seq
                    , gen_date
		)
		VALUES(
				#{seq}
				, #{rentType}
				, #{startDate}
				, #{endDate}
				, #{receiver}
				, #{price}
				, #{status}
				, #{takePlaceCode}
				, #{takePlaceBasic}
				, #{takePlaceDetail}
				, #{merchantUid}
				, #{payMethod}
				, #{carSeq}
				, #{memSeq}
                , #{cmpnSeq}
                , SYSDATE
		)

mapper.update:
UPDATE RENT_HISTORY
SET STATUS = '예약취소'
WHERE MERCHANT_UID = #{merchantUid}

mapper.select:
SELECT SEQ
FROM RENT_HISTORY
WHERE MERCHANT_UID = #{merchantUid}
