mapper.select:
SELECT SEQ , TITLE , CONTENT , WRITER , STATUS , GEN_DATE , UPT_DATE FROM ( SELECT /*+ INDEX_DESC(QNA IDX_QNA) */ ROWNUM RN, SEQ, TITLE, CONTENT, WRITER, STATUS, GEN_DATE, UPT_DATE FROM QNA
AND ROWNUM <= #{pageNum} * #{amount}
) WHERE RN > (#{pageNum} - 1) * #{amount} ORDER BY SEQ DESC

mapper.insert:

	SELECT SEQ_QNA.NEXTVAL FROM DUAL

INSERT INTO QNA ( SEQ
				, TITLE
				, CONTENT
				, WRITER)
VALUES ( #{seq}
	   , #{title}
	   , #{content}
	   , #{writer})

mapper.update:
UPDATE QNA
SET TITLE = #{title}
  , CONTENT = #{content}
  , WRITER = #{writer}
  , UPT_DATE = SYSDATE
WHERE SEQ = #{seq}
